import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Menu = () => {
  const [productList, setProductList] = useState([]);
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [type, setType] = useState('Drink');
  const [size, setSize] = useState('S');
  const [price, setPrice] = useState('');
  const [editIndex, setEditIndex] = useState(-1);
  const [errorMsg, setErrorMsg] = useState('');

  const fetchProducts = async () => {
    try {
      const response = await axios.get('/product/all'); // Update the endpoint accordingly
      setProductList(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const isValidPrice = (input) => {
    return /^\d+$/.test(input);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name || !price) {
      setErrorMsg('Name and Price are required fields');
      return;
    }

    if (!isValidPrice(price)) {
      setErrorMsg('Invalid price format (should be a positive integer)');
      return;
    }

    const requestData = {
      name,
      description,
      type,
      size,
      price: parseFloat(price),
    };

    try {
      if (editIndex === -1) {
        await axios.post('/product/create', requestData);
      } else {
        const productID = productList[editIndex].productID;
        await axios.put(`/product/update/${productID}`, requestData);
      }

      fetchProducts();

      setName('');
      setDescription('');
      setType('Drink');
      setSize('S');
      setPrice('');
      setEditIndex(-1);
      setErrorMsg('');
    } catch (error) {
      console.error(error);
    }
  };

  const handleEdit = (index) => {
    const product = productList[index];
    setName(product.name);
    setDescription(product.description);
    setType(product.type);
    setSize(product.fProducts[0]?.size || 'S');
    setPrice(product.fProducts[0]?.price.toString() || '');
    setEditIndex(index);
  };

  const handleDelete = async (productID) => {
    try {
      await axios.delete(`/product/delete/${productID}`);
      fetchProducts();
    } catch (error) {
      console.error(error);
    }
  };

  const handleTypeChange = (e) => {
    setType(e.target.value);
    setSize(e.target.value === 'Drink' ? 'S' : '');
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Shipper Management</h1>
      {/* Form */}
      <form onSubmit={handleSubmit} className="mb-4">
        <div className="flex mb-2">
          <div className="w-1/2 pr-2">
            <label className="block mb-2 font-semibold">Name:</label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="w-full px-3 py-2 border rounded-lg"
            />
          </div>
          <div className="w-1/2 pl-2">
            <label className="block mb-2 font-semibold">Phone Number:</label>
            <input
              type="tel"
              value={phoneNumber}
              onChange={(e) => setPhoneNumber(e.target.value)}
              className="w-full px-3 py-2 border rounded-lg"
            />
          </div>
        </div>

        {errorMsg && <p className="text-red-500">{errorMsg}</p>}

        <button
          type="submit"
          className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
        >
          {editIndex === -1 ? "Add Shipper" : "Update Shipper"}
        </button>
      </form>

      {/* Table */}
      <div className="max-h-96 overflow-y-auto">
        <table className="w-full border">
          <thead>
            <tr className="bg-gray-200">
              <th className="px-4 py-2">Shipper ID</th>
              <th className="px-4 py-2">Name</th>
              <th className="px-4 py-2">Phone Number</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {shipperList.length > 0 &&
              shipperList.slice(0, 100).map((shipper, index) => (
                <tr
                  key={index}
                  className={index % 2 === 0 ? "bg-gray-100" : ""}
                >
                  <td className="px-4 py-2">{shipper.shipperID}</td>
                  <td className="px-4 py-2">{shipper.name}</td>
                  <td className="px-4 py-2">{shipper.phoneNumber}</td>
                  <td className="px-4 py-2">
                    <button
                      onClick={() => handleEdit(index)}
                      className="px-2 py-1 bg-yellow-500 text-white rounded-md hover:bg-yellow-600 mr-2"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(shipper.shipperID)}
                      className="px-2 py-1 bg-red-500 text-white rounded-md hover:bg-red-600"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Menu;
